export class Component{constructor(e){this.$parent=parseParent(e)}render(){this.$parent.innerHTML=this.template()}updateComponent(e){Object.keys(e).forEach(t=>{this.state[t]=e[t]}),this.render()}destroy(){this.$parent.innerHTML=""}}const parseParent=e=>"string"==typeof e?document.querySelector(e):e,parseTemplate=e=>e;